#!/usr/bin/env python3# -*- coding: UTF-8 -*-'''程序分析：使用 datetime 模块。题目：输出指定格式的日期。'''import datetimeimport timeprint(datetime.date(1941, 1, 5))print(datetime.date.today())print(datetime.date.today().strftime('%d/%m/%Y'))print(time.asctime())print(time.localtime())print(time.strftime('%Y-%m-%d %H:%M:%S', time.localtime()))print(time.time())